        listcustomer.add(new Customer(1, "Nguyễn Văn A", "1999-08-11", "Đà Nẵng","VanA@gmail.com", "data:image/jpeg;base64,"));
        listcustomer.add(new Customer(2, "Nguyễn Văn B", "1998-07-11", "Quảng Trị", "VanB@gmail.com", "data:image/jpeg;base64,"));
        listcustomer.add(new Customer(3, "Nguyễn Văn C", "1997-06-11", "Quảng Nam", "VanC@gmail.com", "https://thietbiketnoi.com/wp-content/uploads/2020/01/tong-hop-hinh-nen-background-vector-designer-dep-do-phan-giai-fhd-2k-4k-moi-nhat-24-1024x678.jpg"));
        listcustomer.add(new Customer(4, "Nguyễn Văn D", "1996-05-11", "Quảng Bình", "VanD@gmail.com", "https://i.pinimg.com/originals/3d/c4/49/3dc449b04d9ace524a0ecd247e1fdc83.png"));
